import React, { useState, useEffect } from "react";
import { ThemeProvider, leftAlign } from "../styles/theme";
import {
  Hero,
  Container,
  Row,
  Column,
  Subheader,
  Header
} from "../styles/common";
import Layout from "../components/layouts";
import TestimonialsList from "../components/testimonials-list";
import { hero, testimonials } from "../content/testimonials-page.json";

const Testimonials = props => {
  const [slideIdx, setSlide] = useState(0);
  useInterval(() => {
    // Your custom logic here
    setSlide(slideIdx + 1);
  }, 3000);

  return (
    <Layout>
      <ThemeProvider>
        <Hero layout={leftAlign}>
          <Container>
            <Row>
              <Column>
                <Header>{hero.header}</Header>
                <Subheader>{hero.subheader}</Subheader>
              </Column>
              <Column>{testimonials[slideIdx].review}</Column>
            </Row>
          </Container>
        </Hero>
      </ThemeProvider>
      <TestimonialsList testimonials={testimonials} />
    </Layout>
  );
};
export default Testimonials;

function useInterval(callback, delay) {
  const savedCallback = useRef();

  // Remember the latest function.
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  // Set up the interval.
  useEffect(() => {
    function tick() {
      savedCallback.current();
    }
    if (delay !== null) {
      let id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
}
